query:
mutation addBasketItem(
  $options: BasketOptionsInput!
  $item: AddBasketItemInput!
  $capabilities: CapabilitiesInput
  $include_token: Boolean!
) {
  get_basket_page: add_basket_item(options: $options, item: $item, capabilities: $capabilities) {
    ...basketFields
  }
}



fragment basketFields on BasketPage {
  meta {
    basket {
      items {
        ...itemsFields
      }
      itemBackupStrategyLabels: item_backup_strategy_labels {
        id
        label
      }
      subtotalBeforeDiscounts: subtotal_before_discounts {
        ...moneyFields
      }
      subtotalFormatted: subtotal_formatted
    }
    summary: basket_page_summary {
      subtotal: summary_text
      url: target_url
    }
    fullPageUpsell: full_page_upsell {
      presentation {
        title
        allergensSubtitle: allergens_subtitle
      }
      menuItems: menu_items {
        id
        name
        is_sponsored_product
        discount_price: discounted_price {
          formatted
          fractional
        }
      }
    }
    requestUUID: request_uuid
    isGroupOrder: is_group_order
    offerMeta: offer_meta {
      ...offerMetaFields
    }
  }
  fulfillment: meta_fulfillment_time_methods {
    fulfillment_method_label: label
    fulfillment_method: method
    asap {
      minuteRange: minute_range
      option_display_label: display_label
      selected_display_label
      selected_time {
        time
        day
      }
      timestamp
    }
    days {
      day
      day_label
      times {
        option_display_label: display_label
        selected_display_label
        selected_time {
          time
          day
        }
        timestamp
      }
    }
  }
  header: ui_header {
    title
    subtitle
    actions {
      ...uiActionFields
    }
  }
  sections: ui_sections {
    ...uiComponentSectionFields
  }
  footer: ui_footer {
    sections: ui_sections {
      ...uiComponentSectionFields
    }
  }
  modalsOnLoad: modals_on_load {
    ...uiModalFields
  }
  modalDefinitions: modal_definitions {
    ...uiModalFields
  }
  metaTimers: meta_timers {
    ...timerFields
  }
}


fragment uiModalFields on UIModal {
  id
  trackingId: tracking_id
  presentationStyle: presentation_style
  header {
    title
    actions {
      ...uiActionFields
    }
  }
  sections {
    ...uiComponentSectionFields
  }
  footer {
    sections {
      ...uiComponentSectionFields
    }
  }
}



fragment baseUiSpanFields on UISpan {
  typeName: __typename
  ... on UISpanText {
    key
    text
    size
    trackingId: tracking_id
    target {
      ...uiTargetFields
    }
    color {
      ...colorFields
    }
    isBold: is_bold
  }
  ... on UISpanSpacer {
    key
    width
  }
  ... on UISpanIcon {
    key
    icon: span_icon {
      name
      size
      color {
        ...colorFields
      }
    }
  }
  ... on UISpanPlusLogo {
    key
    plusLogoSize: size
    plusLogoColor: color {
      ...colorFields
    }
  }
  ... on UISpanCountdown {
    key
    endsAt: ends_at
    size
    color {
      ...colorFields
    }
    isBold: is_bold
  }
}

fragment uiSpanFields on UISpan {
  ...baseUiSpanFields
  ... on UISpanText {
    isStrikethrough: is_strikethrough
  }
  ... on UISpanTag {
    key
    typeName: __typename
    backgroundColor: background_color {
      ...colorFields
    }
    target {
      ...baseUiTargetFields
      ... on UITargetModalAction {
        ...uiTargetModalAction
      }
    }
    uiSpans: ui_spans {
      ... on UISpanText {
        typeName: __typename
        key
        text
        size
        trackingId: tracking_id
        color {
          ...colorFields
        }
        isBold: is_bold
      }
      ... on UISpanSpacer {
        typeName: __typename
        key
        width
      }
      ... on UISpanIcon {
        key
        icon: span_icon {
          name
          size
          color {
            ...colorFields
          }
        }
      }
    }
  }
}



fragment baseUiTargetFields on UITarget {
  typeName: __typename
  ... on UITargetAction {
    params {
      id
      value
    }
    action
    layoutId: layout_id
  }
  ... on UITargetMenuItem {
    menuItemId: menu_item_id
  }
  ... on UITargetWebPage {
    url
    newWindow: new_window
    trackingId: tracking_id
  }
  ... on UITargetLayoutGroup {
    layoutGroupId: layout_group_id
  }
  ... on UITargetMutation {
    params {
      id
      value
    }
    displayName: display_name
    mutation
  }
  ... on UITargetParams {
    title
    params {
      id
      value
    }
    queryParams: query_params
    trackingId: tracking_id
  }
}

fragment uiTargetFields on UITarget {
  ...baseUiTargetFields
  ... on UITargetGoToCheckout {
    __typename
  }
  ... on UITargetShowEditItemModal {
    basketItemLegacyId: basket_item_legacy_id
  }
  ... on UITargetModalAction {
    ...uiTargetModalAction
  }
  ... on UITargetAddBasketItem {
    ...uiTargetAddBasketItem
  }
  ... on UITargetSetScheduledDeliveryTime {
    ...uiTargetSetScheduledDeliveryTime
  }
  ... on UITargetDismissModal {
    ...uITargetDismissModalFields
  }
  ... on UITargetShowModal {
    ...uiTargetShowModalFields
  }
  ... on UITargetGoBack {
    ...uiTargetGoBack
  }
  ... on UITargetGoToBasket {
    ...uiTargetGoToBasket
  }
  ... on UITargetGoToRestaurants {
    ...uiTargetGoToRestaurants
  }
  ... on UITargetSetRadioButtonValue {
    value
  }
  ... on UITargetToggle {
    ...uiTargetToggleFields
  }
  ... on UITargetUpdateCharityDonationOption {
    money: value {
      ...moneyFields
    }
  }
}


fragment uiTargetModalAction on UITargetModalAction {
  trackingId: tracking_id
  action
  params {
    id
    value
